import { Component, Input, type OnInit } from '@angular/core';
import { FormControl, type FormGroup } from '@angular/forms';
import log from 'loglevel';

/**
 * Komponente mit dem Tag &lt;hs-create-art&gt;, um das Erfassungsformular
 * f&uuml;r ein neues Buch zu realisieren.
 */
@Component({
    selector: 'hs-create-newsletter',
    templateUrl: './create-newsletter.component.html',
    styleUrls: ['./create-kunde.component.scss'],
})
export class CreateNewsletterComponent implements OnInit {
    @Input()
    createForm!: FormGroup;

    readonly newsletter = new FormControl(false);

    ngOnInit() {
        log.debug('CreateNewsletterComponent.ngOnInit');
        // siehe formControlName innerhalb @Component({templateUrl: ...})
        this.createForm.addControl('newsletter', this.newsletter);
    }
}
<div for="newsletterInput" [formGroup]="createForm">
  <mat-slide-toggle id="newsletterInput" class="toggle-margin" formControlName="newsletter">
    Newsletter abonnieren
  </mat-slide-toggle>
</div>
