import { Component, Input, type OnInit } from '@angular/core';
import { FormControl, type FormGroup } from '@angular/forms';
import log from 'loglevel';

/**
 * Komponente mit dem Tag &lt;hs-create-art&gt;, um das Erfassungsformular
 * f&uuml;r ein neues Buch zu realisieren.
 */
@Component({
    selector: 'hs-create-geschlecht',
    templateUrl: './create-geschlecht.component.html',
    styleUrls: ['./create-kunde.component.scss'],
})
export class CreateGeschlechtComponent implements OnInit {
    @Input()
    createForm!: FormGroup;

    readonly geschlecht = new FormControl(undefined);

    ngOnInit() {
        log.debug('CreateGeschlechtComponent.ngOnInit');
        // siehe formControlName innerhalb @Component({templateUrl: ...})
        this.createForm.addControl('geschlecht', this.geschlecht);
    }
}
<mat-form-field
  appearance="outline"
  class="userInputField"
  [formGroup]="createForm"
>
  <mat-label>Geschlecht</mat-label>
  <mat-select
    id="geschlechtInput"
    formControlName="geschlecht"
    [class.is-invalid]="geschlecht.invalid && geschlecht.touched"
    [class.is-valid]="geschlecht.valid && geschlecht.touched"
  >
    <mat-option value="M">Männlich</mat-option>
    <mat-option id="Weiblich" value="W">Weiblich</mat-option>
    <mat-option value="D">Divers</mat-option>
  </mat-select>
  <mat-hint>Bitte Geschlecht auswählen</mat-hint>
</mat-form-field>
