import { Component, Input, type OnInit } from '@angular/core';
import { FormControl, type FormGroup, Validators } from '@angular/forms';
import log from 'loglevel';

/**
 * Komponente mit dem Tag "hs-create-ort", um das Erfassungsformular
 * für einen neuen Kunden zu realisieren.
 */
@Component({
    // moduleId: module.id,
    selector: 'hs-create-betrag',
    templateUrl: './create-betrag.component.html',
    styleUrls: ['./create-kunde.component.scss'],
})
export class CreateBetragComponent implements OnInit {
    @Input()
    createForm!: FormGroup;

    // Keine Vorbelegung bzw. der leere String, da es Placeholder gibt
    // Varianten fuer Validierung:
    //    serverseitig mittels Request/Response
    //    clientseitig bei den Ereignissen keyup, change, blur, ...
    // Ein Endbenutzer bewirkt staendig einen neuen Fehlerstatus
    readonly betrag = new FormControl(undefined, [Validators.required]);

    ngOnInit() {
        log.debug('CreateBetragComponent.ngOnInit');
        // siehe formControlName innerhalb @Component({templateUrl: ...})
        this.createForm.addControl('betrag', this.betrag);
    }
}
<mat-form-field
  appearance="outline"
  class="userInputField"
  [formGroup]="createForm"
>
  <mat-label for="betragInput">Umsatz</mat-label>
  <input
    id="betragInput"
    matInput
    placeholder="ex. 4999"
    autocomplete="off"
    [class.is-invalid]="betrag.invalid && betrag.touched"
    [class.is-valid]="betrag.valid && betrag.touched"
    formControlName="betrag"
  />
  <mat-hint>Bitte den Umsatz eingeben</mat-hint>
</mat-form-field>
