import { Component, Input, type OnInit } from '@angular/core';
import { FormControl, type FormGroup } from '@angular/forms';
import log from 'loglevel';

/**
 * Komponente mit dem Tag &lt;hs-create-art&gt;, um das Erfassungsformular
 * f&uuml;r ein neues Buch zu realisieren.
 */
@Component({
    selector: 'hs-create-interessen',
    templateUrl: './create-interessen.component.html',
    styleUrls: ['./create-kunde.component.scss'],
})
export class CreateInteressenComponent implements OnInit {
    @Input()
    createForm!: FormGroup;

    readonly sport = new FormControl(false);

    readonly lesen = new FormControl(false);

    readonly reisen = new FormControl(false);

    ngOnInit() {
        log.debug('CreateInteressenComponent.ngOnInit');
        // siehe formControlName innerhalb @Component({templateUrl: ...})
        this.createForm.addControl('sport', this.sport);
        this.createForm.addControl('lesen', this.lesen);
        this.createForm.addControl('reisen', this.reisen);
    }
}
<section class="interessen-section" [formGroup]="createForm">
  Interessen:
  <mat-checkbox id="sportInput" class="interessen-margin" formControlName="sport"
    >Sport</mat-checkbox
  >
  <mat-checkbox id="reisenInput" class="interessen-margin" formControlName="reisen"
    >Reisen</mat-checkbox
  >
  <mat-checkbox id="lesenInput" class="interessen-margin" formControlName="lesen"
    >Lesen</mat-checkbox
  >
</section>
