import { Component, Input, type OnInit } from '@angular/core';
import { FormControl, type FormGroup, Validators } from '@angular/forms';
import { type GeschlechtType } from '../shared/kunde';
import log from 'loglevel';

/**
 * Komponente f&uuml;r das Tag <code>hs-update-art</code>
 */
@Component({
    selector: 'hs-update-geschlecht',
    templateUrl: './update-geschlecht.component.html',
    styleUrls: ['./update.component.scss'],
})
export class UpdateGeschlechtComponent implements OnInit {
    // <hs-update-art [form]="form" [currentValue]="...">
    @Input()
    updateForm!: FormGroup;

    @Input()
    currentValue!: GeschlechtType;

    geschlecht!: FormControl;

    ngOnInit() {
        log.debug(
            'UpdateArtComponent.ngOnInit: currentValue=',
            this.currentValue,
        );
        // siehe formControlName innerhalb @Component({templateUrl: ...})
        this.geschlecht = new FormControl(
            this.currentValue,
            Validators.required,
        );
        this.updateForm.addControl('geschlecht', this.geschlecht);
    }
}
<mat-form-field
  appearance="outline"
  class="updateInputField"
  [formGroup]="updateForm"
>
  <mat-label>Geschlecht</mat-label>
  <mat-select
    formControlName="geschlecht"
    required
    [class.is-invalid]="geschlecht.invalid && geschlecht.touched"
    [class.is-valid]="geschlecht.valid && geschlecht.touched"
  >
    <mat-option value="M">Männlich</mat-option>
    <mat-option value="W">Weiblich</mat-option>
    <mat-option value="D">Divers</mat-option>
  </mat-select>
  <mat-hint>Bitte Geschlecht auswählen</mat-hint>
</mat-form-field>
